INSERT INTO `answer` VALUES ('1758', '438', 'Vẫn để công nhân đang làm việc bình thường, tiến hành thực hiện sửa chữa và tiếp đất lại theo đúng quy định.', 'B');
INSERT INTO `answer` VALUES ('1759', '438', 'Ngừng ngay công việc và yêu cầu công nhân đang làm việc trên đường dây xuống, tìm hiểu và xác định nguyên nhân rõ ràng.  ', 'C');
INSERT INTO `answer` VALUES ('1760', '438', 'Yêu cầu về lấy tiếp địa khác để thay thế và cho tiếp tục làm việc.', 'D');
INSERT INTO `answer` VALUES ('1761', '439', 'Cắt máy cắt và đưa ra vị trí thí nghiệm.', 'A');
