INSERT INTO `answer` VALUES ('1769', '441', 'Đơn vị công tác không thực hiện đầy đủ các biện pháp an toàn cô lập khu vực công tác để ngăn ngừa các nguồn điện có thể dẫn đến vị trí công tác.', 'A');
