INSERT INTO `answer` VALUES ('1774', '442', 'Người chỉ huy trực tiếp không tăng cường nhân lực để tháo dây dẫn vướng gàu.', 'B');
INSERT INTO `answer` VALUES ('1775', '442', 'Người chỉ huy trực tiếp khi thấy nguy cơ xảy ra tai nạn không báo cáo cho Lãnh đạo đơn vị để chỉ đạo giải quyết.', 'C');
INSERT INTO `answer` VALUES ('1776', '442', 'Không đề ra và thực hiện các biện pháp xử lý đảm bảo an toàn hơn như hạ cần gàu hoặc dùng sào thao tác chống/đẩy dây ra.', 'D');
INSERT INTO `answer` VALUES ('1777', '443', 'Thực hiện theo chỉ đạo của người chỉ huy trực tiếp để ngăn ngừa sự cố.', 'A');
INSERT INTO `answer` VALUES ('1778', '443', 'Thông báo cho nhân viên vận hành biết công việc thực hiện để không cho đóng điện lại sớm.', 'B');
INSERT INTO `answer` VALUES ('1779', '443', 'Từ chối thực hiện vì công việc trên nằm ngoài phạm vi công tác, không đảm bảo an toàn.', 'C');
